  `zip` varchar(10) NOT NULL DEFAULT '' COMMENT '邮编',
  `phone` varchar(15) NOT NULL DEFAULT '' COMMENT '固定电话',
  `mobile` varchar(15) NOT NULL DEFAULT '' COMMENT '手机号',
  `remark` varchar(400) NOT NULL DEFAULT '' COMMENT '订单附言',
  `delivery_time` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '配送时间',
  `gold_score` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '订单送的金象积分',
  `silver_score` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '订单送的银象积分',
  `pay_mode` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '0=用钱购买；1=金象积分；2=银象积分；换购',
  `invoice` varchar(50) NOT NULL DEFAULT '' COMMENT '发票抬头',
  `order_type` tinyint(1) unsigned DEFAULT '0' COMMENT '订单类型:0=普通订单',
