  `divide_user_id_2` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '提成的二级用户ID',
  `divide_user_score_gold_1` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '一级用户返的金象积分',
  `divide_user_score_silver_1` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '一级用户返的银象积分',
  `divide_user_score_gold_2` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '二级用户返的金象积分',
  `divide_user_score_silver_2` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '二级用户返的银象积分',
  `deductible_score` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '换购积分数量',
  `use_deductible_score_gold` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '订单占值：deductible_score = use_deductible_score_gold+use_deductible_score_silver',
  `use_deductible_score_silver` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '订单占值：deductible_score = use_deductible_score_gold+use_deductible_score_silver',
  `deductible_score_gold` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '用掉多少金象积分',
  `deductible_score_silver` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '用掉多少银象积分',
  `is_comment_to_seller` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '商家被评论：1＝已评论；0＝未评论',
  `is_comment_to_user` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '买家被评论：1＝已评论；0＝未评价',
  `is_delete` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '1=删除；0=未删除',
  `out_trade_no` varchar(64) NOT NULL DEFAULT '' COMMENT '微信支付关联成功的订单号',
  `expires` int(11) NOT NULL COMMENT '订单有效期',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=90 DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

