  `old_presenter_id` int(11) NOT NULL DEFAULT '0' COMMENT '以前的推荐用户，恢复数据用的',
  `username` varchar(50) CHARACTER SET utf8 NOT NULL,
  `password` varchar(32) CHARACTER SET utf8 NOT NULL,
  `nickname` varchar(50) NOT NULL DEFAULT '' COMMENT '昵称',
  `real_name` varchar(50) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '真实姓名',
  `qq_number` varchar(20) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT 'QQ号',
  `wangwang_number` varchar(50) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '旺旺号',
  `weixin` varchar(50) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '微信号',
  `email` varchar(100) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '邮箱',
  `add_time` int(11) NOT NULL,
  `display` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '状态：0＝未激活；1＝已激活；2＝冻结',
  `total` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '账户余额',
  `score_gold` int(11) NOT NULL DEFAULT '0' COMMENT '金象积分余额',
  `score_silver` int(11) NOT NULL DEFAULT '0' COMMENT '银象积分余额',
  `experience` int(11) NOT NULL DEFAULT '0' COMMENT '经验值',
  `total_gold` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '金象币余额',
  `total_silver` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '银象币余额',
  `login_time` int(11) NOT NULL DEFAULT '0' COMMENT '登录时间',
  `ip` varchar(20) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT 'IP',
  `ip_address` varchar(50) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT 'IP对应的地址',
  `mobile` varchar(20) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '手机号',
  `phone` varchar(20) CHARACTER SET utf8 NOT NULL DEFAULT '',
  `zip` char(6) CHARACTER SET utf8 NOT NULL DEFAULT '',
  `address` varchar(100) CHARACTER SET utf8 NOT NULL DEFAULT '',
  `sex` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '0=保密 1=男 2=女',
  `path` varchar(200) CHARACTER SET utf8 DEFAULT NULL COMMENT '用户头像',
  `birthday` date NOT NULL,
  `presenter_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '推荐者用户ID',
  `presenter_username` varchar(50) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '推荐者用户名',
  `par_presenter_id` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '推广人的上级用户ID',
  `par_presenter_username` varchar(50) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '推广人的上级用户名',
  `remark_time` int(11) NOT NULL DEFAULT '0' COMMENT '设置推荐人时间',
  `remark` varchar(140) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '设置推荐人的备注',
  `pop_code` varchar(32) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '推广码',
  `admin_id` int(11) NOT NULL DEFAULT '0' COMMENT '操作员ID，哪个业务发展的推广员',
  `admin_add_time` int(11) NOT NULL DEFAULT '0' COMMENT '操作时间',
  `wx_openid` varchar(64) NOT NULL DEFAULT '',
  `wx_unionid` varchar(64) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '微信open_id',
  `province_id` int(11) NOT NULL DEFAULT '0',
  `city_id` int(11) NOT NULL DEFAULT '0',
  `area_id` int(11) NOT NULL DEFAULT '0',
  `txt_address` varchar(100) CHARACTER SET utf8 NOT NULL DEFAULT '',
  `qq_unionid` varchar(64) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT 'qq第三方登录openid',
  `wb_uid` varchar(24) CHARACTER SET utf8 DEFAULT '',
  `pay_password` varchar(48) CHARACTER SET utf8 DEFAULT '' COMMENT '支付密码',
  `is_robot` tinyint(1) DEFAULT '0' COMMENT '0=真实用户，1=机器人',
  `ad_text` varchar(60) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '广告词',
  `push_cid` varchar(100) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '个推用户cid',
  `total_gold_rmb_pre` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '金象积分兑换人民币比例',
  `total_silver_rmb_pre` int(11) unsigned NOT NULL DEFAULT '1' COMMENT '银象积分兑换人民币比例',
  `alipay_account` varchar(50) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '支付宝提现账号',
  `weixin_account` varchar(50) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '微信提现账号',
  `ebank_account` varchar(50) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '银联提现账号',
  `gold_card_num` varchar(50) CHARACTER SET utf8 NOT NULL DEFAULT '' COMMENT '金象卡号',
  `wx_code_path` varchar(255) NOT NULL DEFAULT '' COMMENT '小程序码',
  PRIMARY KEY (`id`),
  KEY `presenter_id_index` (`presenter_id`),
  KEY `par_presenter_id_index` (`par_presenter_id`)
) ENGINE=InnoDB AUTO_INCREMENT=91 DEFAULT CHARSET=utf8mb4 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

