  `score_type` enum('gold','silver') NOT NULL DEFAULT 'gold' COMMENT '币的类型',
  `score_num` decimal(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '币的数量',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

