INSERT INTO `product` VALUES ('207', '0', '颜色', '尺码', '中国人保车险', 's', '', '', '&lt;h2 style=\"text-align: center;\" microsoft=\"\"&gt;中国人民财产保险股份有限公司&lt;/h2&gt;&lt;p microsoft=\"\"&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=\"margin: 0px 0px 24px; padding: 0px; color: rgb(50, 50, 50); line-height: 26px; text-indent: 2em; letter-spacing: normal; font-family: 宋体; font-size: 14px; font-style: normal; font-weight: normal; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; background-color: rgb(255, 255, 255); font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px;\"&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;中国人民财产保险股份有限公司（&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;PICC P&amp;amp;C&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;，简称&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;&ldquo;&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;中国人保财险&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;&rdquo;&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;）的前身是&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;1949&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;年&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;10&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;月&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;20&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;日成立的中国人民保险公司，是&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;&ldquo;&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;世界&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;500&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;强&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;&rdquo;&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;中国人民保险集团股份有限公司（&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;PICC&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;）的核心成员和标志性主业。&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;2003&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;年，中国人保财险在香港联交所主板挂牌上市（股票代码&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;2328&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;）。&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;2016&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;年，公司实现发展新跨越，保费收入突破&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;3000&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;亿元。&lt;/span&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/span&gt;&amp;nbsp;&lt;/p&gt;&lt;p style=\"margin: 0px 0px 24px; padding: 0px; color: rgb(50, 50, 50); line-height: 26px; text-indent: 2em; letter-spacing: normal; font-family: 宋体; font-size: 14px; font-style: normal; font-weight: normal; word-spacing: 0px; white-space: normal; orphans: 2; widows: 2; background-color: rgb(255, 255, 255); font-variant-ligatures: normal; font-variant-caps: normal; -webkit-text-stroke-width: 0px;\"&gt;&lt;span style=\"line-height: 1; font-family: 宋体; font-size: 16px;\"&gt;中国人保财险始终坚持党的领导，始终坚决服从并贯彻落实党的大政方针和国家各项决